settings.custom_css:
selector .wdk-categories-carousel_arrow {
    transform: translate(-50%,-50%)!important;
    left: initial !important;
    right: initial !important;
} 

selector .slick-slide .wdk-slider-item:before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(255, 255, 255, 0.5);
    transition: all 0.4s ease-in-out;
    z-index: 10;
}

selector .slick-slide.slick-current.slick-active .wdk-slider-item:before {
    opacity: 0;
    visibility: visible;
    background-color: rgba(0, 0, 0, 0.35);
}

 @media (max-width:1200px){
 selector .wdk-categories-carousel_arrow {
        display: none !important;
    } 
}
